import React from 'react';
import ReactDOM from 'react-dom';

class ReloadableGif extends React.Component {
    constructor(props){
      super(props)
      this.state = {
        gif: '../images/filaCircular.jpg',
        loaded: '../images/filaCircular.jpg'
      }
    }
    
    reloadGif = () => {
      this.setState({loaded: ''})
      setTimeout(() => {
        this.setState({loaded: this.state.gif})
      }, 0)
    }
    
    render(){
      return <div>
        <img src={this.state.loaded} alt="FILA CIRCULAR"/>
        <button onClick={this.reloadGif}>Replay Animation</button>
      </div>
    }
  }
  
  ReactDOM.render(
    <ReloadableGif />,
    document.getElementById("react")
  );
